import React, { useState } from "react";
import "../AgeCalc.css";

export default function AgeCalculator() {
  const [year, setYear] = useState("0");
  const [month, setMonth] = useState("0");
  const [day, setDay] = useState("0");
  const [age, setAge] = useState("");

  const [nextBirthdayMonths, setNextBirthdayMonths] = useState("0");
  const [nextBirthdayDays, setNextBirthdayDays] = useState("0");

  let todaysDateString = new Date().toLocaleDateString();

  const [dateOfBirth, setDateOfBirth] = useState("");
  const [todaysDate, setTodaysDate] = useState(todaysDateString);

  const [errorDateOfBirth, setDateOfBirthError] = useState("");
  const [errorTodaysDate, setTodaysDateError] = useState("");

  const [totalYears, setTotalYears] = useState("0");
  const [totalMonths, setTotalMonths] = useState("0");
  const [totalWeek, setTotalWeek] = useState("0");
  const [totalHours, setTotalHours] = useState("0");
  const [totalMinutes, setTotalMinutes] = useState("0");
  const [totalSeconds, setTotalSeconds] = useState("0");

  const calculateAge = () => {
    if (dateOfBirth === "" || todaysDate === "") {
      dateOfBirth === ""
        ? setDateOfBirthError("Enter Valid Date : DD-MM-YYYY")
        : setDateOfBirthError("");
      todaysDate === ""
        ? setTodaysDateError("Enter Valid Date : DD-MM-YYYY")
        : setTodaysDateError("");

      setYear("0");
      setMonth("0");
      setDay("0");
    } else {
      setDateOfBirthError("");
      setTodaysDateError("");

      let birth = dateOfBirth.split("/");
      let birthDate1 = {
        birthYear: parseInt(birth[2]),
        birthMonth: parseInt(birth[1]),
        birthDay: parseInt(birth[0]),
      };
      // let birthYear = parseInt(birth[2]);
      // let birthMonth = parseInt(birth[1]);
      // let birthDay = parseInt(birth[0]);

      const currentDate = new Date();
      const birthDate = new Date(
        birthDate1.birthYear,
        birthDate1.birthMonth - 1,
        birthDate1.birthDay
      ); // Note: month is zero-based

      if (birthDate > currentDate) {
        setYear("");
        setMonth("");
        setDay("");
        setDateOfBirthError("Invalid birth date");
        return;
      }

      let ageYears = currentDate.getFullYear() - birthDate.getFullYear();
      let ageMonths = currentDate.getMonth() - birthDate.getMonth();
      let ageDays = currentDate.getDate() - birthDate.getDate();

      if (ageMonths < 0 || (ageMonths === 0 && ageDays < 0)) {
        ageYears--;
        ageMonths += 12;
      }

      if (ageDays < 0) {
        const prevMonthDate = new Date(
          currentDate.getFullYear(),
          currentDate.getMonth() - 1,
          0
        );
        ageDays += prevMonthDate.getDate();
        ageMonths--;
      }

      let nextBM = Math.abs(birthDate1.birthMonth - 1 - currentDate.getMonth());
      setNextBirthdayMonths(`${nextBM}`);
      setYear(ageYears);
      setMonth(ageMonths);
      setDay(ageDays);

      let seconds = currentDate.getTime() - birthDate.getTime();

      setTotalYears(ageYears);
      setTotalSeconds(seconds);
    }
  };

  function clear() {
    setDateOfBirth("");
    setTodaysDate("");
  }

  return (
    <>
      <div className="container p-2">
        <div className="text-center">
          <h1 className="text-info">Age Calculator</h1>
        </div>
        <hr />
        <div className="row">
          <span className="col-5">
            <h5 className="text-primary">Today's Date</h5>
          </span>
          <span id="inputError" className="col-7 text-danger text-end">
            {errorTodaysDate}
          </span>
        </div>
        <div>
          <input
            className="form-control"
            placeholder="DD/MM/YYYY"
            value={todaysDate}
            onChange={(e) => setTodaysDate(e.target.value)}
          ></input>
        </div>
        <div className="row mt-2 pt-2">
          <span className="col-5 text-primary">
            <h5>Date of Birth</h5>
          </span>
          <span id="inputError" className="col-7 text-danger text-end">
            {errorDateOfBirth}
          </span>
        </div>
        <div>
          <input
            className="form-control"
            placeholder="DD/MM/YYYY"
            value={dateOfBirth}
            onChange={(e) => setDateOfBirth(e.target.value)}
          ></input>
        </div>
        <div className="text-center mt-3 pt-3">
          <button
            className="btn btn-primary w-50 p-2"
            onClick={() => calculateAge()}
          >
            Calculate
          </button>
          <span className="mx-1"></span>
          <button className="btn btn-primary w-25 p-2" onClick={() => clear()}>
            Clear
          </button>
        </div>
        <div>{age}</div>
        <div className="mt-2 pt-2">
          <hr />
          <h5 className="text-success">Age</h5>
          <div className="mx-5 border border-primary">
            <table className="table table-light">
              <th className="text-center">Year</th>
              <th className="text-center">Months</th>
              <th className="text-center">Days</th>
              <tbody>
                <td className="text-center text-primary">{year}</td>
                <td className="text-center text-primary">{month}</td>
                <td className="text-center text-primary">{day}</td>
              </tbody>
            </table>
          </div>
          <div className="mt-2 pt-2">
            <hr />
            <h5 className="text-success">Next Birthday</h5>
            <div className="mx-5 mt-2 border border-primary">
              <table className="table table-light">
                <th className="text-center">Months</th>
                <th className="text-center">Days</th>
                <tbody>
                  <td className="text-center text-primary">
                    {nextBirthdayMonths}
                  </td>
