import React, { useState } from "react";
import "../AgeCalc.css";

export default function AgeCalculator() {
  const [year, setYear] = useState("0");
  const [month, setMonth] = useState("0");
  const [day, setDay] = useState("0");

  const [birthDateDay, setBirthDateDay] = useState("");

  const [currentDateDay, setCurrentDateDay] = useState(
    new Date().toUTCString().substring(0, 3)
  );

  const [nextBirthdayMonths, setNextBirthdayMonths] = useState("0");
  const [nextBirthdayDays, setNextBirthdayDays] = useState("0");

  let todaysDateString = new Date().toLocaleDateString();

  const [dateOfBirth, setDateOfBirth] = useState("");
  console.log("dateOfBirth" + dateOfBirth);
  const [todaysDate, setTodaysDate] = useState(todaysDateString);

  const [errorDateOfBirth, setDateOfBirthError] = useState("");
  const [errorTodaysDate, setTodaysDateError] = useState("");

  const [totalYears, setTotalYears] = useState("0");
  const [totalMonths, setTotalMonths] = useState("0");
  const [totalWeek, setTotalWeek] = useState("0");
  const [totalHours, setTotalHours] = useState("0");
  const [totalMinutes, setTotalMinutes] = useState("0");
  const [totalSeconds, setTotalSeconds] = useState("0");

  const [developerName, setDeveloperName] = useState(
    "Developer : Sagar Ghumare"
  );

  setTimeout(() => {
    setDeveloperName("");
  }, 4000);

  const calculateAge = () => {
    if (dateOfBirth === "" || todaysDate === "") {
      if (dateOfBirth === "") {
        setBirthDateDay("");
        setDateOfBirthError("Enter Valid Date : DD/MM/YYYY");
      } else {
        setDateOfBirthError("");
      }

      if (todaysDate === "") {
        setTodaysDateError("Enter Valid Date : DD/MM/YYYY");
        setCurrentDateDay("");
      } else {
        setTodaysDateError("");
      }

      setYear("0");
      setMonth("0");
      setDay("0");
    } else {
      setDateOfBirthError("");
      setTodaysDateError("");

      let birth = dateOfBirth.split("-");
      let birthDate1 = {
        birthYear: parseInt(birth[0]),
        birthMonth: parseInt(birth[1]),
        birthDay: parseInt(birth[2]),
      };

      let todayD1 = todaysDate.split("/");
      let todaysDate1 = {
        birthYear: parseInt(todayD1[2]),
        birthMonth: parseInt(todayD1[1]),
        birthDay: parseInt(todayD1[0]),
      };

      const currentDate = new Date(
        todaysDate1.birthYear,
        todaysDate1.birthMonth - 1,
        todaysDate1.birthDay
      );
      const currentDate1 = new Date(
        todaysDate1.birthYear,
        todaysDate1.birthMonth - 1,
        todaysDate1.birthDay + 1
      );
      const birthDate = new Date(
        birthDate1.birthYear,
        birthDate1.birthMonth - 1,
        birthDate1.birthDay + 1
      ); // Note: month is zero-based

      if (birthDate > currentDate) {
        setYear("");
        setMonth("");
        setDay("");
        setBirthDateDay("");
        setDateOfBirthError("Invalid birth date");
        return;
      }

      setCurrentDateDay(currentDate1.toUTCString().substring(0, 3));
      setBirthDateDay(birthDate.toUTCString().substring(0, 3));

      let ageYears = currentDate.getFullYear() - birthDate.getFullYear();
      let ageMonths = currentDate.getMonth() - birthDate.getMonth();
      let ageDays = currentDate.getDate() - birthDate.getDate();

      if (ageMonths < 0 || (ageMonths === 0 && ageDays < 0)) {
        ageYears--;
        ageMonths += 12;
      }

      if (ageDays < 0) {
        const prevMonthDate = new Date(
          currentDate.getFullYear(),
          currentDate.getMonth() - 1,
          0
        );
        ageDays += prevMonthDate.getDate();
        ageMonths--;
      }

      setNextBirthdayMonths(
        `${Math.abs(birthDate1.birthMonth - 1 - currentDate.getMonth())}`
      );
      setYear(ageYears);
      setMonth(ageMonths);
      setDay(ageDays);

      let seconds = Math.floor(
        (currentDate.getTime() - birthDate.getTime()) / 1000
      );
      let minutes = Math.floor(seconds / 60);
      let hours = Math.floor(minutes / 60);
      let week = Math.floor(hours / 168);
      let month = Math.floor(hours / 730);

      setNextBirthdayDays(30 - ageDays);
      setTotalYears(ageYears);
      setTotalSeconds(seconds);
      setTotalMinutes(minutes);
      setTotalHours(hours);
      setTotalWeek(week);
      setTotalMonths(month);
    }
  };

  function clear() {
    setDateOfBirth("");
    setTodaysDate("");
  }

  return (
    <>
      <div className="container p-2">
        <div className="text-center">
          <span className="fs-1">Age Calculator</span>
        </div>
        <hr />
        <div id="dev" className="text-center text-underline fw-medium">
          <a href="" className="text-success-emphasis">
            {developerName}
          </a>
        </div>
        <div className="row">
          <span className="col-5">
            <h5 className="text-primary">Today's Date</h5>
          </span>
          <span id="inputError" className="col-7 text-danger text-end">
            <span className="text-success">{currentDateDay}</span>
            {errorTodaysDate}
          </span>
        </div>
        <div>
          <input
            className="form-control"
            placeholder="dd/mm/yyyy"
            value={todaysDate}
            onChange={(e) => setTodaysDate(e.target.value)}
          ></input>
        </div>
        <div className="row mt-2 pt-2">
          <span className="col-5 text-primary">
            <h5>Date of Birth</h5>
          </span>
          <span id="inputError" className="col-7 text-danger text-end">
            <span className="text-success">{birthDateDay}</span>
            {errorDateOfBirth}
          </span>
        </div>
        <div>
          <input
            type="date"
            min="1950-01-01"
            max="2030-12-31"
            className="form-control"
            placeholder="DD-MM-YYYY"
            value={dateOfBirth}
            onChange={(e) => setDateOfBirth(e.target.value.toString())}
          ></input>
        </div>
        <div className="text-center mt-3 pt-3">
          <button
            className="btn btn-primary w-50 p-2"
            onClick={() => calculateAge()}
          >
            Calculate
          </button>
          <span className="mx-1"></span>
          <button className="btn btn-primary w-25 p-2" onClick={() => clear()}>
            Clear
          </button>
        </div>
        <div className="mt-2 pt-2">
          <hr />
          <h5 className="text-success-emphasis">Age</h5>
          <div className="mx-5 border border-primary">
            <table className="table table-light">
              <th className="text-center">Year</th>
              <th className="text-center">Months</th>
              <th className="text-center">Days</th>
              <tbody>
                <td className="text-center text-primary">{year}</td>
                <td className="text-center text-primary">{month}</td>
                <td className="text-center text-primary">{day}</td>
              </tbody>
            </table>
          </div>
          <div className="mt-2 pt-2">
            <hr />
            <h5 className="text-success">Next Birthday</h5>
            <div className="mx-5 mt-2 border border-primary">
              <table className="table table-light">
                <th className="text-center">Months</th>
                <th className="text-center">Days</th>
                <tbody>
                  <td className="text-center text-primary">
                    {nextBirthdayMonths}
                  </td>
                  <td className="text-center text-primary">
                    {nextBirthdayDays}
                  </td>
                </tbody>
              </table>
            </div>
          </div>
          <div className="mt-2 pt-2">
            <hr />
            <h5 className="text-success">Extra</h5>
            <div className="mx-5 mt-2 border border-primary">
              <table className="table table-light">
                <tbody>
                  <tr>
                    <td className="text-center">Total Years</td>
                    <td className="text-start text-primary">{totalYears}</td>
                  </tr>
                  <tr>
                    <td className="text-center">Total Months</td>
                    <td className="text-start text-primary">{totalMonths}</td>
                  </tr>
                  <tr>
                    <td className="text-center">Total Week</td>
                    <td className="text-start text-primary">{totalWeek}</td>
                  </tr>
                  <tr>
                    <td className="text-center">Total Hours</td>
                    <td className="text-start text-primary">{totalHours}</td>
                  </tr>
                  <tr>
                    <td className="text-center">Total Minutes</td>
                    <td className="text-start text-primary">{totalMinutes}</td>
                  </tr>
                  <tr>
                    <td className="text-center">Total Seconds</td>
                    <td className="text-start text-primary">{totalSeconds}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
